sys_variable_value.value:
var cis = new GlideRecord('task_ci');
var q = 'task=' + current.sys_id;
cis.addEncodedQuery(q);
cis.query();
while(cis.next()) {
	var inc = new GlideRecord('incident');
	inc.initialize();
	inc.assigned_to = current.assigned_to;
	inc.assignment_group = current.assignment_group;
	inc.short_description = current.short_description;
	inc.description = current.description;
	inc.cmdb_ci = cis.ci_item;
	inc.parent = current.sys_id;
	inc.insert();
}

