// set global workflow variables to be used in various scripts

var start = new GlideDateTime();
var currentDateTime = new GlideDateTime();
var dates = [];

start.setValue(current.u_start_fixed_date_time);

workflow.scratchpad.currentDateTime = currentDateTime.getDisplayValue();
workflow.scratchpad.startDateTime = start.getDisplayValue();

if (current.u_recurrence_type == 'week') {
    for (var i = 1, ii = current.u_recurrence_interval; i < ii; i++) {
        //start.addDaysLocalTime('7');
        start.addSeconds('30');
        dates.push(start.getDisplayValue());
    }

}
if (current.u_recurrence_type == 'month') {
    for (var i = 1, ii = current.u_recurrence_interval; i < ii; i++) {
        start.addMonthsLocalTime('1');
        dates.push(start.getDisplayValue());
    }
}

workflow.scratchpad.datesArray = dates;

gs.info("Kontrollit workflow ::: workflow scratchpad currentDateTime: {0}", workflow.scratchpad.currentDateTime);
gs.info("Kontrollit workflow ::: workflow scratchpad startDateTime: {0}", workflow.scratchpad.startDateTime);
