// Set 'answer' to the number of seconds this timer should wait

var currentDateTime = new GlideDateTime();
var dd = new GlideDateTime();
dd.setValue(currentDateTime.getDisplayValue());

var answer = false;
for (var i = 0, ii = workflow.scratchpad.datesArray.length; i < ii; i++) {
	var d = new GlideDateTime();
	d.setValue(workflow.scratchpad.datesArray[i]);
	gs.info("kontrollit workflow ::: dd currentDateTime: {0}, d is the compared date: {1}", dd.getNumericValue(), d.getNumericValue());
	gs.info("kontrollit workflow ::: before shift. date [{0}]{1}", i, workflow.scratchpad.datesArray[i]);
	if (d.getNumericValue() > dd.getNumericValue() && !answer) {

		answer = (d.getNumericValue() - dd.getNumericValue()) / 1000;

	}
}

workflow.scratchpad.datesArray.shift();
for (var i = 0, ii = workflow.scratchpad.datesArray.length; i < ii; i++) {
	gs.info("kontrollit workflow ::: after shift. date [{0}]{1}", i, workflow.scratchpad.datesArray[i]);
}

